/*
   Showing the following pattern?
   
       (1)*             (2) * * * * *
          * *               * * * *
          * * *              * * *
          * * * *             * *   
          * * * * *           *
 */

package programming_challenge;

/**
 * 
 */
public class challenge_1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("*");
		System.out.println("* *");
		System.out.println("* * *");
		System.out.println("* * * *");
		System.out.println("* * * * *");
		
		System.out.println("---------------------");
		
		System.out.println("* * * * *");
		System.out.println("* * * * ");
		System.out.println("* * * ");
		System.out.println("* * ");
		System.out.println("*");
		
		System.out.println("------------------------");
		
		
	}

}
